import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';

// Connect to the server
const socket = io('http://localhost:5000');
const [users, setUsers] = useState([]);
const [selectedUser, setSelectedUser] = useState(null);
const [room, setRoom] = useState('general');

function App() {
  const [user, setUser] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [name, setName] = useState('');
  const [role, setRole] = useState('student'); // Default role
  const messagesEndRef = useRef(null);

  // Auto-scroll to the bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
	  socket.on('userJoined', (userData) => {
  setUsers(prev => [...prev, userData]);
  // Rest of your code...
});
socket.on('userLeft', (userData) => {
  setUsers(prev => prev.filter(user => user.id !== userData.id));
  // Rest of your code...
});

    scrollToBottom();
  }, [messages]);

  // Socket event listeners
  useEffect(() => {
    // Handle incoming messages
    socket.on('message', (messageData) => {
      setMessages(prevMessages => [...prevMessages, messageData]);
    });

    // Handle user joining
    socket.on('userJoined', (userData) => {
      setMessages(prevMessages => [
        ...prevMessages,
        {
          system: true,
          text: `${userData.name} joined the chat`,
          timestamp: new Date().toISOString()
        }
      ]);
    });

    // Handle user leaving
    socket.on('userLeft', (userData) => {
      setMessages(prevMessages => [
        ...prevMessages,
        {
          system: true,
          text: `User left the chat`,
          timestamp: new Date().toISOString()
        }
      ]);
    });

    // Clean up on unmount
    return () => {
      socket.off('message');
      socket.off('userJoined');
      socket.off('userLeft');
    };
  }, []);

  // Handle joining the chat
  const handleJoin = (e) => {
    e.preventDefault();
    if (!name.trim()) return;

    // Join the chat
    const userData = { name, role };
    socket.emit('join', userData);
    setUser(userData);
  };

  // Handle sending messages
  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!message.trim() || !user) return;

    // Send the message
    const messageData = {
      text: message,
      sender: user.name,
      role: user.role,
      timestamp: new Date().toISOString()
    };
    socket.emit('message', messageData);
    setMessage('');
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Secure Chat Application</h1>
      </header>

      {!user ? (
        // Login form
        <div className="login-container">
          <form onSubmit={handleJoin} className="login-form">
            <h2>Join the Chat</h2>
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
            <select 
              value={role} 
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="student">Student</option>
              <option value="lecturer">Lecturer</option>
            </select>
            <button type="submit">Join</button>
          </form>
        </div>
      ) : (
        // Chat interface
        <div className="chat-container">
          <div className="messages-container">
            {messages.map((msg, index) => (
              <div 
                key={index} 
                className={`message ${msg.system ? 'system-message' : 
                  msg.sender === user.name ? 'my-message' : 'other-message'}`}
              >
                {!msg.system && (
                  <div className="message-header">
                    <span className="sender">{msg.sender}</span>
                    <span className="role">{msg.role}</span>
                    <span className="time">
                      {new Date(msg.timestamp).toLocaleTimeString()}
                    </span>
                  </div>
                )}
                <div className="message-text">{msg.text}</div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
          <form onSubmit={handleSendMessage} className="message-form">
            <input
              type="text"
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
            <button type="submit">Send</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
